import "next-auth"

declare module "next-auth" {
    interface Session {
        user: {
            id: string
            name?: string | null
            email?: string | null
            image?: string | null
            credits: number
            role: "USER" | "ADMIN"
        }
    }

    interface User {
        credits: number
        role: "USER" | "ADMIN"
    }
}

declare module "@auth/core/adapters" {
    interface AdapterUser {
        credits: number
        role: "USER" | "ADMIN"
    }
}
